import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Home = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // Función para cargar usuario
  const fetchUser = () => {
    const tokens = JSON.parse(localStorage.getItem("tokens"));
    if (!tokens) {
      logout(); // Si no hay tokens, cerrar sesión y redirigir
      return;
    }

    axios
      .get("http://localhost:4000/me", {
        headers: { Authorization: `Bearer ${tokens.access_token}` },
      })
      .then((res) => setUser(res.data))
      .catch(() => logout()); // Si falla la solicitud, cerrar sesión
  };

  useEffect(() => {
    fetchUser();
  }, []);

  // Función para cerrar sesión
const logout = () => {
  // Limpiar tokens locales
  localStorage.removeItem("tokens");
  setUser(null);

  // Redirigir al logout de ZITADEL
  const clientId = "338010317902660978"; // tu client_id
  const returnTo = encodeURIComponent("http://localhost:4000/login");
  const logoutUrl = `https://interle-jy3ptw.us1.zitadel.cloud/oauth/v2/logout?client_id=${clientId}&returnTo=${returnTo}`;

  window.location.href = logoutUrl;
};


  return (
    <div className="flex flex-col items-center justify-center h-screen">
      {user ? (
        <>
          <h1 className="text-2xl mb-4">Bienvenido {user.name}</h1>
          <pre className="bg-gray-200 p-4 rounded mb-4">{JSON.stringify(user, null, 2)}</pre>
          <button
            onClick={logout}
            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          >
            Cerrar sesión
          </button>
        </>
      ) : (
        <h1>Cargando usuario...</h1>
      )}
    </div>
  );
};

export default Home;
